        });
    }

    // Smooth scrolling for anchor links (fallback for browsers that don't support CSS scroll-smooth)
    const anchorLinks = document.querySelectorAll('a[href^="#"]');
    anchorLinks.forEach(link => {
        link.addEventListener('click', function(e) {
            const targetId = this.getAttribute('href');

            // Skip if href is just "#"
            if (targetId === '#') {
                e.preventDefault();
                window.scrollTo({ top: 0, behavior: 'smooth' });
                return;
            }

            const targetElement = document.querySelector(targetId);
            if (targetElement) {
                e.preventDefault();

                // Get the offset for the fixed nav
                const navHeight = document.querySelector('nav').offsetHeight;
                const targetPosition = targetElement.offsetTop - navHeight;

                window.scrollTo({
                    top: targetPosition,
                    behavior: 'smooth'
                });
            }
        });
    });

    // Active nav link highlighting on scroll
    const sections = document.querySelectorAll('section[id]');
    const navLinks = document.querySelectorAll('nav a[href^="#"]');

    function highlightNavLink() {
        const scrollPosition = window.scrollY + 100; // Offset for fixed nav

        sections.forEach(section => {
            const sectionTop = section.offsetTop;
            const sectionHeight = section.offsetHeight;
            const sectionId = section.getAttribute('id');

            if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
                navLinks.forEach(link => {
                    link.classList.remove('text-blue-600');
                    link.classList.add('text-gray-700');

                    if (link.getAttribute('href') === `#${sectionId}`) {
                        link.classList.remove('text-gray-700');
                        link.classList.add('text-blue-600');
                    }
                });
            }
        });
    }

    // Add scroll event listener for nav highlighting
    window.addEventListener('scroll', highlightNavLink);

    // Add fade-in animation to elements when they come into view
    const observerOptions = {
        threshold: 0.1,
        rootMargin: '0px 0px -50px 0px'
    };

    const fadeInObserver = new IntersectionObserver(function(entries) {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.style.opacity = '0';
                entry.target.style.transform = 'translateY(20px)';

                setTimeout(() => {
                    entry.target.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
                    entry.target.style.opacity = '1';
                    entry.target.style.transform = 'translateY(0)';
                }, 100);

                fadeInObserver.unobserve(entry.target);
            }
        });
    }, observerOptions);

    // Observe project cards for fade-in animation
    const projectCards = document.querySelectorAll('#projects > div > div > div');
    projectCards.forEach(card => {
        fadeInObserver.observe(card);
    });

    // Keyboard navigation support for disabled buttons (accessibility)
    const disabledButtons = document.querySelectorAll('button[disabled]');
    disabledButtons.forEach(button => {
        button.setAttribute('aria-disabled', 'true');
    });

    // Console message for visitors who check the console
    console.log('%c Hello, curious developer!', 'color: #3B82F6; font-size: 20px; font-weight: bold;');
    console.log('%cInterested in the code? Check out the repository on GitHub!', 'color: #6B7280; font-size: 14px;');
    console.log('%chttps://github.com/seanreed1111/seanreed1111.github.io', 'color: #3B82F6; font-size: 14px;');
});

// Handle window resize to close mobile menu if window is resized to desktop size
window.addEventListener('resize', function() {
    const mobileMenu = document.getElementById('mobile-menu');
    const mobileMenuButton = document.getElementById('mobile-menu-button');

    if (window.innerWidth >= 768 && mobileMenu && !mobileMenu.classList.contains('hidden')) {
        mobileMenu.classList.add('hidden');
        const icon = mobileMenuButton.querySelector('i');
        if (icon) {
            icon.classList.remove('fa-times');
            icon.classList.add('fa-bars');
        }
    }
});

