/* Custom styles to supplement Tailwind CSS */

/* Smooth scrolling is handled by HTML scroll-smooth class, but adding fallback */
html {
    scroll-behavior: smooth;
}

/* Custom animations */
@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.fade-in {
    animation: fadeIn 0.6s ease-out;
}

/* Ensure sections have proper spacing from fixed nav */
section {
    scroll-margin-top: 80px;
}

/* Custom hover effects for links */
a {
    transition: all 0.3s ease;
}

/* Custom gradient background for hero */
#hero {
    background: linear-gradient(135deg, #EBF4FF 0%, #FFFFFF 50%, #E0E7FF 100%);
}

/* Project card hover effect enhancement */
.project-card {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.project-card:hover {
    transform: translateY(-5px);
}

/* Skills badge animation on hover */
.skill-badge {
    transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.skill-badge:hover {
    transform: scale(1.05);
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

/* Mobile menu transition */
#mobile-menu {
    transition: max-height 0.3s ease-in-out;
    overflow: hidden;
}

/* Accessibility: Focus styles */
a:focus,
button:focus {
    outline: 2px solid #3B82F6;
    outline-offset: 2px;
}

/* Print styles */
@media print {
    nav,
    footer {
        display: none;
    }

    section {
        page-break-inside: avoid;
    }
}

/* Custom scrollbar styling (for webkit browsers) */
::-webkit-scrollbar {
    width: 10px;
}

::-webkit-scrollbar-track {
    background: #f1f1f1;
}

::-webkit-scrollbar-thumb {
    background: #3B82F6;
    border-radius: 5px;
}

::-webkit-scrollbar-thumb:hover {
    background: #2563EB;
}

