'use client';

import { Project } from '@/types';
import { cn } from '@/lib/utils';
import Image from 'next/image';
import { AnimatedSection } from './AnimatedSection';

interface ProjectCardProps {
  project: Project;
  delay?: number;
}

export function ProjectCard({ project, delay = 0 }: ProjectCardProps) {
  const isComingSoon = project.status === 'coming-soon';

  return (
    <AnimatedSection delay={delay}>
      <article
        className={cn(
          'group relative overflow-hidden rounded-xl border bg-white shadow-md',
          'transition-all duration-300 hover:shadow-xl hover:-translate-y-1',
          isComingSoon && 'opacity-75'
        )}
      >
        {/* Image */}
        <div className="relative aspect-video w-full overflow-hidden bg-gray-100">
          <Image
            src={project.imageUrl}
            alt={project.title}
            fill
            className="object-cover transition-transform duration-300 group-hover:scale-105"
          />
          {isComingSoon && (
            <div className="absolute inset-0 flex items-center justify-center bg-black/50">
              <span className="text-lg font-semibold text-white">Coming Soon</span>
            </div>
          )}
        </div>

        {/* Content */}
        <div className="p-6">
          <h3 className="text-xl font-bold text-gray-900 mb-2">
            {project.title}
          </h3>
          <p className="text-gray-600 mb-4 line-clamp-2">
            {project.description}
          </p>

          {/* Technologies */}
          <div className="flex flex-wrap gap-2 mb-4">
            {project.technologies.map((tech) => (
              <span
                key={tech}
                className="rounded-md bg-gray-100 px-2.5 py-1 text-xs font-medium text-gray-700"
              >
                {tech}
              </span>
            ))}
          </div>

          {/* Links */}
          {!isComingSoon && (
            <div className="flex gap-4">
              {project.githubUrl && (
                <a
                  href={project.githubUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-sm font-medium text-blue-600 hover:text-blue-700 hover:underline"
                >
                  View Code <i className="fas fa-external-link-alt ml-1" />
                </a>
              )}
              {project.liveUrl && (
                <a
                  href={project.liveUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-sm font-medium text-blue-600 hover:text-blue-700 hover:underline"
                >
                  Live Demo <i className="fas fa-external-link-alt ml-1" />
                </a>
              )}
            </div>
          )}
        </div>
      </article>
    </AnimatedSection>
  );
}
```

