import { type ClassValue, clsx } from 'clsx'
import { twMerge } from 'tailwind-merge'

/**
 * Merges class names with Tailwind CSS classes
 * Combines clsx for conditional classes and tailwind-merge for deduplication
 */
export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

/**
 * Smoothly scrolls to a section with offset for fixed navigation
 * @param sectionId - The ID of the section to scroll to
 * @param offset - The offset in pixels (default: 80 for navigation height)
 */
export function scrollToSection(sectionId: string, offset: number = 80) {
  const element = document.getElementById(sectionId)
  if (element) {
    const elementPosition = element.getBoundingClientRect().top
    const offsetPosition = elementPosition + window.pageYOffset - offset

    window.scrollTo({
      top: offsetPosition,
      behavior: 'smooth',
    })
  }
}

'use client'

import { useState, useEffect } from 'react'
import { NAV_HEIGHT } from '@/lib/constants'

/**
 * Hook to determine which section is currently in view
 * @param sectionIds - Array of section IDs to track
 * @returns The ID of the currently active section
 */
export function useActiveSection(sectionIds: string[]) {
  const [activeSection, setActiveSection] = useState<string>('')

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.pageYOffset + NAV_HEIGHT + 100

      // Find the section that is currently in view
      for (const sectionId of sectionIds) {
        const element = document.getElementById(sectionId)
        if (element) {
          const { offsetTop, offsetHeight } = element
          if (
            scrollPosition >= offsetTop &&
            scrollPosition < offsetTop + offsetHeight
          ) {
            setActiveSection(sectionId)
            break
          }
        }
      }

      // If at the top of the page, set to first section
      if (window.pageYOffset < 100) {
        setActiveSection(sectionIds[0] || '')
      }
    }

    // Set initial active section
    handleScroll()

    window.addEventListener('scroll', handleScroll, { passive: true })

    return () => {
      window.removeEventListener('scroll', handleScroll)
    }
  }, [sectionIds])

  return activeSection
}

'use client'

import { navLinks } from '@/data/navigation'
import { personalInfo } from '@/data/personal-info'
import { useScrollPosition } from '@/hooks/useScrollPosition'
import { useActiveSection } from '@/hooks/useActiveSection'
import { scrollToSection } from '@/lib/utils'
import { cn } from '@/lib/utils'
import { SECTIONS } from '@/lib/constants'
import { HiMenu } from 'react-icons/hi'

interface NavigationProps {
  onMenuClick: () => void
}

export default function Navigation({ onMenuClick }: NavigationProps) {
  const scrollPosition = useScrollPosition()
  const activeSection = useActiveSection([
    SECTIONS.HOME,
    SECTIONS.ABOUT,
    SECTIONS.PROJECTS,
    SECTIONS.CONTACT,
  ])

  const isScrolled = scrollPosition > 50

  const handleNavClick = (href: string) => {
    const sectionId = href.replace('#', '')
    scrollToSection(sectionId)
  }

  return (
    <nav
      className={cn(
        'fixed top-0 left-0 right-0 z-50 transition-all duration-300',
        isScrolled
          ? 'bg-white dark:bg-gray-900 shadow-md'
          : 'bg-transparent'
      )}
    >
      <div className="container mx-auto px-6">
        <div className="flex items-center justify-between h-20">
          {/* Logo */}
          <button
            onClick={() => scrollToSection('home')}
            className={cn(
              'text-2xl font-bold transition-colors duration-300',
              isScrolled
                ? 'text-gray-900 dark:text-white'
                : 'text-gray-900 dark:text-white'
            )}
          >
            {personalInfo.name.split(' ')[0]}
          </button>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center gap-8">
            {navLinks.map((link) => {
              const sectionId = link.href.replace('#', '')
              const isActive = activeSection === sectionId

              return (
                <button
                  key={link.id}
                  onClick={() => handleNavClick(link.href)}
                  className={cn(
                    'text-base font-medium transition-colors duration-200 relative group',
                    isActive
                      ? 'text-blue-600 dark:text-blue-400'
                      : isScrolled
                      ? 'text-gray-700 dark:text-gray-300 hover:text-blue-600 dark:hover:text-blue-400'
                      : 'text-gray-900 dark:text-white hover:text-blue-600 dark:hover:text-blue-400'
                  )}
                >
                  {link.label}
                  <span
                    className={cn(
                      'absolute bottom-0 left-0 w-full h-0.5 bg-blue-600 dark:bg-blue-400 transform transition-transform duration-200',
                      isActive ? 'scale-x-100' : 'scale-x-0 group-hover:scale-x-100'
                    )}
                  />
                </button>
              )
            })}
          </div>

          {/* Mobile Menu Button */}
          <button
            onClick={onMenuClick}
            className={cn(
              'md:hidden p-2 rounded-lg transition-colors duration-200',
              isScrolled
                ? 'text-gray-900 dark:text-white hover:bg-gray-100 dark:hover:bg-gray-800'
                : 'text-gray-900 dark:text-white hover:bg-gray-100 dark:hover:bg-gray-800'
            )}
            aria-label="Open menu"
          >
            <HiMenu className="w-6 h-6" />
          </button>
        </div>
      </div>
    </nav>
  )
}

/**
 * Application constants
 */

