export const NAV_HEIGHT = 80

// Section IDs for navigation
export const SECTIONS = {
  HOME: 'home',
  ABOUT: 'about',
  PROJECTS: 'projects',
  CONTACT: 'contact',
} as const

// Animation delays
export const ANIMATION_DELAYS = {
  SHORT: 100,
  MEDIUM: 200,
  LONG: 300,
